@roblox/global/restorefunction.code_sample:
local old; old = hookfunction(print, function(...)
	return old("[hooked]", ...)
end)

print("Hello!") -- Output: [hooked] Hello!

restorefunction(print)
print("Hello!") -- Output: Hello!

@roblox/global/getrawmetatable.code_sample:
local exampleTable = {}
local meta = {
	__index = function(tbl, key)
		return "Custom value for " .. tostring(key)
	end,
	__metatable = "Protected metatable"
}
setmetatable(exampleTable, meta)

print(getmetatable(exampleTable)) -- Output: Protected metatable

local rawMeta = getrawmetatable(exampleTable)
print(rawMeta == meta) -- Output: true
print(exampleTable.nonexistent) -- Output: Custom value for nonexistent

@roblox/global/setrawmetatable.code_sample:
local exampleTable = {}
local originalMeta = {
	__index = function()
		return "Original value"
	end,
	__metatable = "Protected metatable"
}
setmetatable(exampleTable, originalMeta)

local newMeta = {
	__index = function()
		return "New value"
	end
}

print(exampleTable.test) -- Output: Original value
setrawmetatable(exampleTable, newMeta)
print(exampleTable.test) -- Output: New value

@roblox/global/request.code_sample:
local response = request({
	Url = "https://httpbin.org/get",
	Method = "GET",
	Headers = {
		["Content-Type"] = "application/json"
	}
})

if response.Success then
	print("Status:", response.StatusCode)
	print("Body:", response.Body)
else
	print("Request failed:", response.StatusMessage)
end
